@testset "Raw HTML" begin

    # Example 613
    input = "<a><bab><c2c>\n"
    md = Markdown.parse(input; flavor=:julia)
    expected = "<p><a><bab><c2c></p>\n"
    actual = Markdown.html(md)
    @test_broken expected == actual

    # Example 614
    input = "<a/><b2/>\n"
    md = Markdown.parse(input; flavor=:julia)
    expected = "<p><a/><b2/></p>\n"
    actual = Markdown.html(md)
    @test_broken expected == actual

    # Example 615
    input = "<a  /><b2\ndata=\"foo\" >\n"
    md = Markdown.parse(input; flavor=:julia)
    expected = "<p><a  /><b2\ndata=\"foo\" ></p>\n"
    actual = Markdown.html(md)
    @test_broken expected == actual

